let burgerButtons = document.querySelector('.header__buttons')
let burger = document.querySelector('.burger')
let body = document.querySelector('body');

burger.addEventListener('click', function () {
	burgerButtons.classList.toggle('_active');
	burger.classList.toggle('_active');
	body.classList.toggle('_lock')
})
// -----------------------------------------

let windowWidth = window.innerWidth;
let blockSlide = document.querySelector('.tarif__row');
let stepSlide = document.querySelector('.step__body');
let reviewSlide = document.querySelector('.reviews__row');

if (windowWidth <= 968) {
	blockSlide.classList.add('_swiper')
} else {
	blockSlide.classList.remove('_swiper')
}
if (windowWidth <= 775) {
	stepSlide.classList.add('_swiper')
} else {
	stepSlide.classList.remove('_swiper')
}
if (windowWidth <= 1110) {
	reviewSlide.classList.add('_swiper')

} else {
	reviewSlide.classList.remove('_swiper')
}
//BildSlider
let sliders = document.querySelectorAll('._swiper');
if (sliders) {
	for (let index = 0; index < sliders.length; index++) {
		let slider = sliders[index];
		if (!slider.classList.contains('swiper-bild')) {
			let slider_items = slider.children;
			if (slider_items) {
				for (let index = 0; index < slider_items.length; index++) {
					let el = slider_items[index];
					el.classList.add('swiper-slide');
				}
			}
			let slider_content = slider.innerHTML;
			let slider_wrapper = document.createElement('div');
			slider_wrapper.classList.add('swiper-wrapper');
			slider_wrapper.innerHTML = slider_content;
			slider.innerHTML = '';
			slider.appendChild(slider_wrapper);
			slider.classList.add('swiper-bild');

			if (slider.classList.contains('_swiper_scroll')) {
				let sliderScroll = document.createElement('div');
				sliderScroll.classList.add('swiper-scrollbar');
				slider.appendChild(sliderScroll);
			}
		}
		if (slider.classList.contains('_gallery')) {
			//slider.data('lightGallery').destroy(true);
		}
	}
	sliders_bild_callback();
}

function sliders_bild_callback(params) { }

let sliderScrollItems = document.querySelectorAll('._swiper_scroll');
if (sliderScrollItems.length > 0) {
	for (let index = 0; index < sliderScrollItems.length; index++) {
		const sliderScrollItem = sliderScrollItems[index];
		const sliderScrollBar = sliderScrollItem.querySelector('.swiper-scrollbar');
		const sliderScroll = new Swiper(sliderScrollItem, {
			observer: true,
			observeParents: true,
			direction: 'vertical',
			slidesPerView: 'auto',
			freeMode: true,
			scrollbar: {
				el: sliderScrollBar,
				draggable: true,
				snapOnRelease: false
			},
			mousewheel: {
				releaseOnEdges: true,
			},
		});
		sliderScroll.scrollbar.updateSize();
	}
}


